	public static void main(String[] args) {

		List<FiredRule> firedRules = null;//. getRecommendationInput();
		
		//. load rules...
		
		 ObjectMapper mapper = new ObjectMapper();
		    try {
		    	Map<String, List<Map<String, Object>>> rules = mapper.readValue(loadStringRules(), new TypeReference<Map<String, List<Map<String, Object>>>>() {});
		    	String jsonFact = "{\"Current Activity\":\"LyingDown\",\"Activity Duration\":\"1h\",\"Activity Environment\":\"BedRest\",\"Activity Status\":\"Non-continuous\"}";
		        Map<String, Object> fact = buildFact(jsonFact);		        
		    	PatternMatcher patternMatcher = new PatternMatcher();
		    	firedRules = patternMatcher.fireRule(fact, rules.get("rules"));		    	
		    } catch (Exception e) {
		      e.printStackTrace();
		    }		
		//. endl load rules...	
		
		String userId = "35";
        RecInterpreter interpreter = new RecInterpreter();
        try{
        	firedRules.remove(0);
        	LOG.error("Fired Rules: " + firedRules.size());
        	InterpreterPacket packet = new InterpreterPacket();
        	InterpretationIngredients ingredients = new InterpretationIngredients();
        	ingredients.setFiredRule(firedRules);
        	ingredients.setUserID(userId);
        	packet.setRecommendationInput(ingredients);
           
        	interpreter.interpretRecommendation(packet);
        	
        	List<InterpretedRecommendations> output = packet.getRecommendationOutput();
        	
        	
        	for(InterpretedRecommendations r : output){
        		LOG.error("RecDescription: " + r.getRecDescription());
        		LOG.error("RecUrl: " + r.getRecURL());
        		LOG.error("RecActivity: " + r.getRecActivity());
        		LOG.error("ActDuration: " + r.getActDuration());
        	}
        }
        catch(Exception exp){
            LOG.debug(exp.getMessage());
            exp.printStackTrace();
        }
	}
	
